mapper.select:
select info_id infoId, info_title infoTitle from information where origin_id=#{originId}
and user_id=#{userId} and info_type=#{infoType} and state='A' order by create_time
desc

mapper.update:
update information
info_title = #{infoTitle,jdbcType=VARCHAR}, theme = #{theme,jdbcType=VARCHAR}, thumb_image = #{thumbImage,jdbcType=VARCHAR}, views = #{views,jdbcType=INTEGER}, comments = #{comments,jdbcType=INTEGER}, loves = #{shares,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, is_top = #{isTop,jdbcType=CHAR}, info_type = #{infoType,jdbcType=VARCHAR}, user_id = #{userId,jdbcType=INTEGER}, origin_id = #{originId,jdbcType=INTEGER}, state = #{state,jdbcType=CHAR}, state_time = #{stateTime,jdbcType=TIMESTAMP}, content = #{content,jdbcType=LONGVARCHAR},
where info_id = #{infoId,jdbcType=INTEGER}

update information 
set comments=(comments+1)
where info_id=#{infoId}

mapper.select:
SELECT loves
FROM information
WHERE info_id=#{infoId}
