mapper.update:
update origin
origin_name = #{originName,jdbcType=VARCHAR}, origin_type = #{originType,jdbcType=VARCHAR}, origin_grade = #{originGrade,jdbcType=CHAR}, members = #{members,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, mgr_id = #{creatorId,jdbcType=INTEGER}, state = #{state,jdbcType=CHAR}, state_time = #{stateTime,jdbcType=TIMESTAMP}, exp1 = #{exp1,jdbcType=VARCHAR}, exp2 = #{exp2,jdbcType=VARCHAR}, origin_desc = #{originDesc,jdbcType=LONGVARCHAR},
where origin_id = #{originId,jdbcType=INTEGER}

update origin 
set state=#{state}
where origin_id=#{id}
